--
-- Структура таблицы `bulgakov`
--

CREATE TABLE `bulgakov` (
  `id` int(3) NOT NULL,
  `title` varchar(50) DEFAULT NULL,
  `img` varchar(255) DEFAULT NULL,
  `text` text DEFAULT NULL,
  `price` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `bulgakov`
--

INSERT INTO `bulgakov` (`id`, `title`, `img`, `text`, `price`) VALUES
(1, 'М.А. Булгаков \"Мастер и Маргарита\"', '/img/bulgakov/1.jpg', '«Ма́стер и Маргари́та» — роман Михаила Афанасьевича Булгакова, работа над которым началась в конце 1920-х годов и продолжалась вплоть до смерти писателя. Роман относится к незавершённым произведениям; редактирование и сведение воедино черновых записей осуществляла после смерти мужа вдова писателя — Елена Сергеевна.', 350),
(2, 'М.А. Булгаков \"Дни Турбиных\"', '/img/bulgakov/2.jpg', '«Дни Турбины́х» — пьеса М. А. Булгакова, написанная на основе романа «Белая гвардия». Существует в трёх редакциях. ', 250),
(3, 'М.А. Булгаков \"Бег\"', '/img/bulgakov/3.jpg', '«Бег» — пьеса Михаила Булгакова, написанная в 1926—1927 годах.', 120),
(4, 'М.А. Булгаков \"Кабала святош\"', '/img/bulgakov/4.jpg', '«Кабала святош» — пьеса в четырёх действиях М. А. Булгакова, написанная в 1929 году. Ставилась также под названием «Мольер». Основная тема пьесы, придающая ей автобиографический мотив, — трагедия писателя в условиях жестокой и бездушной тирании.', 190),
(5, 'М.А. Булгаков \"Зойкина квартира\"', '/img/bulgakov/5.jpg', '«Зо́йкина кварти́ра» — пьеса Михаила Булгакова. Премьера пьесы состоялась в Театре имени Вахтангова 28 октября 1926 года. Пьеса состоит из 3 актов. В первом акте — 3 картины, во втором и третьем акте — по две картины.', 120),
(6, 'М.А. Булгаков \"Иван Васильевич\"', '/img/bulgakov/6.jpg', '«Иван Васильевич» — пьеса Михаила Булгакова, работа над которой велась в 1934—1936 годах. Несколько редакций произведения были созданы на основе социальной антиутопии «Блаженство».', 250),
(7, 'М.А. Булгаков \"Собачье сердце\"', '/img/bulgakov/7.jpg', '«Собачье сердце» — повесть Михаила Афанасьевича Булгакова.', 360),
(8, 'М.А. Булгаков \"Белая гвардия\"', '/img/bulgakov/8.jpg', '«Бе́лая гва́рдия» — первый роман Михаила Булгакова. Описываются события Гражданской войны на Украине в конце 1918 года. Роман повествует о семье русских интеллигентов и их друзьях, которые переживают социальный катаклизм гражданской войны.', 300),
(9, 'М.А. Булгаков \"Записки юного врача\"', '/img/bulgakov/9.jpg', '«Записки юного врача» — цикл рассказов М. А. Булгакова, опубликованных в 1925—1926 годах в журналах «Медицинский работник» и «Красная панорама» В цикл входят рассказы «Полотенце с петухом», «Стальное горло», «Крещение поворотом», «Вьюга», «Морфий», «Тьма египетская», «Пропавший глаз» и «Звёздная сыпь».', 270),
(10, 'М.А. Булгаков \"Роковые яйца\"', '/img/bulgakov/10.jpg', '«Роковые я́йца» — фантастическая повесть Михаила Булгакова, впервые опубликованная в 1925 году. Также печаталась в сокращённом виде под названием «Луч жи́зни» в том же году. Написавший повесть «Роковые яйца» в 1924 году Булгаков помещает своих героев в 1928 год.', 310);

