CREATE TABLE `duma` (
  `id` int(3) NOT NULL,
  `title` varchar(50) DEFAULT NULL,
  `img` varchar(255) DEFAULT NULL,
  `text` text DEFAULT NULL,
  `price` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `duma`
--

INSERT INTO `duma` (`id`, `title`, `img`, `text`, `price`) VALUES
(1, 'Александр Дюма \"Три мушкетёра\"', '/img/duma/1.jpg', '«Три мушкетёра» — историко-приключенческий роман Александра Дюма-отца, впервые опубликованный в парижской газете Le Siècle в 1844 году с 14 марта по 11 июля.', 1200),
(2, 'Александр Дюма \"Граф Монте-Кристо\"', '/img/duma/2.jpg', '«Граф Мо́нте-Кри́сто» — приключенческий роман Александра Дюма, классика французской литературы, написанный в 1844—1846 годах.', 1300),
(3, 'Александр Дюма \"Двадцать лет спустя\"', '/img/duma/3.jpg', '«Двадцать лет спустя» — историко-приключенческий роман французского писателя Александра Дюма — продолжение романа «Три мушкетёра», вторая книга трилогии Александра Дюма-отца о королевских мушкетёрах.', 980),
(4, 'Александр Дюма \"Чёрный тюльпан\"', '/img/duma/4.jpg', '«Чёрный тюльпан» — исторический роман Александра Дюма-отца, посвящённый драматическим событиям 1672 года, вошедшего в голландскую историю как «год бедствий». Одноимённый французский приключенческий фильм 1963 года сюжетно не связан с романом Дюма.', 700),
(5, 'Александр Дюма \"Виконт де Бражелон\"', '/img/duma/5.jpg', '«Виконт де Бражелон, или Десять лет спустя» — роман французского писателя Александра Дюма, третья часть трилогии романов о трёх мушкетёрах и д’Артаньяне. Описываются события, происходящие через десять лет после событий описанных в книге «Двадцать лет спустя». Была написана в 1847—1850 годах', 1150),
(6, 'Александр Дюма \"Королева Марго\"', '/img/duma/6.jpg', '«Королева Марго» — исторический роман Александра Дюма-отца, опубликованный в 1845 году и образующий первую часть трилогии о гугенотских войнах, которую продолжают книги «Графиня де Монсоро» и «Сорок пять»', 460),
(7, 'Александр Дюма \"Графиня де Монсоро\"', '/img/duma/7.jpg', '«Графиня де Монсоро» — роман Александра Дюма-отца, созданный в соавторстве с Огюстом Маке и опубликованный в 1846 году. Название произведения связано с графами, владельцами знаменитого замка Монсоро. Это второй роман трилогии Дюма о гугенотских войнах.', 600),
(8, 'Александр Дюма \"Жозеф Бальзамо\"', '/img/duma/8.jpg', '«Жозеф Бальзамо» — исторический роман Александра Дюма-отца, первая часть тетралогии «Записки врача», посвящённой событиям последней трети XVIII века. Русскоязычным читателям роман также известен под названием «Записки врача» и «Джузеппе Бальзамо»', 1400),
(9, 'Александр Дюма \"Ожерелье королевы\"', '/img/duma/9.jpg', '«Ожерелье королевы» — вторая часть тетралогии Александра Дюма-отца, объединённой похождениями известного мага, предсказателя судеб и вечного человека Джузеппе Бальзамо.', 780),
(10, 'Александр Дюма \"Сорок пять\"', '/img/duma/10.jpg', '«Сорок пять» — роман Александра Дюма-отца, написанный в 1847 году и завершающий трилогию о гугенотских войнах — финал истории, которая была начата в романе «Королева Марго» и продолжена в «Графине де Монсоро».', 450);

