--
-- Структура таблицы `month`
--

CREATE TABLE `month` (
  `id` int(3) NOT NULL,
  `title` varchar(50) DEFAULT NULL,
  `img` varchar(255) DEFAULT NULL,
  `text` text DEFAULT NULL,
  `price` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `month`
--

INSERT INTO `month` (`id`, `title`, `img`, `text`, `price`) VALUES
(1, 'Хелен Девитт “Последний самурай”', '/img/bestfor21/1.jpg', 'Лучшим романом признали дебютную работу Хелен Девитт. Главный герой романа — мальчик Людос, который живет со своей мамой Сибиллой в скромной лондонской квартирке и ничего не знает  о своем отце. Мать усердно занимается всесторонним развитием ребенка — уже в четыре года он читает “Иллиаду”, затем — знает несколько иностранных языков, а потом решает найти отца.\r\n\r\nНачало книги, написанное от лица матери, изобилует подробностями. Тяжело продраться через избыточные детали и энциклопедические сведения. Однако терпеливый читатель будет вознагражден.', 300),
(2, 'Джонатан Франзен “Поправки”', '/img/bestfor21/2.jpg', 'Впервые опубликованный всего за 10 дней жо 11 сентября, роман Франзена стал энциклопедией современной американской жизни. Семейная сага о не очень-то счастливых Ламбертах вобрала в себя множество тем и прославила Джонатана Франзена в США и за пределами страны. Неприглядные, но такие живые герои романа заставляют вот уже 17 лет следить за всеми перипетиями сюжета читателей по всему миру.', 400),
(3, 'Кадзуо Исигуро “Не отпускай меня”', '/img/bestfor21/3.png', 'Нобелевский лауреат 2017 года британец японского происхождения Кадзуо Исигуро с безупречной точностью препарирует в своих романах человеческие души.\r\n“Не отпускай меня” — пугающая книга воспоминаний некой Кэти Х. о ее детстве в стенах интерната и юности уже за его пределами. Действие антиутопии происходит в Британии конца XX века, где стало распространенным клонирование людей.', 250),
(4, 'Даниил Гранин \"Мой лейтенант\"', '/img/bestfor21/best1.jpg', 'Кто готовится увидеть очередную глянцевую картинку войны — с победными маршами, патриотическими настроениями и громкими подвигами — может сразу отложить эту книгу. Новый роман Даниила Гранина — это взгляд на Великую Отечественную с изнанки, не с точки зрения генералов и маршалов, спокойно отправлявших в пекло и мясорубку целые армии, а изнутри, из траншей и окопов.\r\nНа фоне тягот, ужасов и неприглядности войны автор дает возможность выговориться простому лейтенанту, одному из тех, кому мы обязаны своей победой. Тех, о чьей смерти официальные сводки Информбюро сообщали как о «незначительных потерях в боях местного значения». Тех, кто вряд ли выбрал себе такую судьбу, будь на то их собственная воля.', 550),
(5, 'Харуки Мураками \"Норвежский лес\"', '/img/bestfor21/best4.jpg', '«Норвежский лес» — роман 1987 года японского писателя Харуки Мураками. Название романа — устоявшийся перевод на японский название песни «Битлз» Norwegian Wood. ', 350),
(6, 'Пауло Коэльо \"Дьявол и сеньорита Прим\"', '/img/bestfor21/best2.jpg', 'Этот роман - удивительный рассказ о человеческой душе, о необходимости делать моральный выбор.\r\n\r\nВ маленьком горном селении появляется незнакомец. В чемодане у него одиннадцать слитков золота, с их помощью он хочет испытать людей, которые, по его мнению, более склонны к злу, чем к добру. Селянам предстоит прожить неделю, полную драматических событий, оказаться перед лицом любви, смерти, власти, в душе каждого будет происходить борьба между Светом и Тьмой.', 250),
(7, 'Алексей Иванов \"Золото бунта\"', '/img/bestfor21/best3.jpg', 'Новый роман пермского писателя посвящен событиям, происходившим на Урале в конце XVIII века, спустя четыре года после разгрома Пугачевского восстания. Герой книги, молодой сплавщик Остафий Переход, должен разгадать загадку гибели своего отца, чтобы смыть с родового имени пятно позора.\r\n\r\nУвлекательный детективный сюжет-автор погружает в таинственный и завораживающий мир реки Чусовой. Здесь караваны барок, груженных железом, стремительно летят по течению мимо смертельно опасных скал – бойцов. Здесь власть купцов и заводчиков ничто в сравнении с могуществом старцев – учителей веры, что правят Рекой из тайных раскольничьих скитов. Здесь даже те, кто носит православный крест, искренне верят в силу вогульских шаманов. Здесь ждет в земле казна Пугачева, золото бунта, клад, который уже четыре года не дастся ни шаманам, ни разбойникам, ни бродягам-пытарям. Клад, который был закопан четырьмя разбойниками братьями Гусевыми. Клад, дорогу к которому знал лишь бесследно пропавший отец молодого сплавщика Остафия Перехода.', 180),
(8, 'Иэн Макьюэн “Искупление”', '/img/bestfor21/8.jpg', 'Роман современного британского классика Иэна Макьюэна рассказывает историю рокового поступка, который исковеркал жизни сразу нескольких людей. Брайони, живущая в мире своих фантазий девочка, обвиняет в изнасиловании, которого тот не совершал, возлюбленного своей сестры, сына садовника Робби. Всю последующую жизнь Брайони, ставшая писательницей, пытается искупить свою ошибку.', 230),
(9, 'Джоан Дидион “Год магического мышления”', '/img/bestfor21/9.jpg', 'Писательница и журналистка Джоан Дидион была награждена за “Год магического мышления” Национальной книжной  премией. В этой книге Джоан Дидион рассказывает об утрате сначала мужа, писателя Джона Грегори Данна, внезапно умершего от сердечного приступа, а потом и дочери Кинтаны, которая была тяжело больна. Для Дидион собранные в книге обрывки воспоминаний о любимых людях, попытка описать ее эмоции до и после потерь — это, безусловно, терапия.', 400),
(10, 'Грегори Дэвид Робертс \"Шантарам\"', '/img/bestfor21/10.jpg', 'Эта преломленная в художественной форме исповедь человека, который сумел выбраться из бездны и уцелеть, протаранила все списки бестселлеров и заслужила восторженные сравнения с произведениями лучших писателей нового времени, от Мелвилла до Хемингуэя. Подобно автору, герой этого романа много лет скрывался от закона. Лишенный после развода с женой родительских прав, он пристрастился к наркотикам, совершил ряд ограблений и был приговорен австралийским судом к девятнадцати годам заключения. Бежав на второй год из тюрьмы строгого режима, он добрался до Бомбея, где был фальшивомонетчиком и контрабандистом, торговал оружием и участвовал в разборках индийской мафии, а также нашел свою настоящую любовь, чтобы вновь потерять ее, чтобы снова найти…', 500);

