--
-- Структура таблицы `pushkin`
--

CREATE TABLE `pushkin` (
  `id` int(3) NOT NULL,
  `title` varchar(50) DEFAULT NULL,
  `img` varchar(255) DEFAULT NULL,
  `text` text DEFAULT NULL,
  `price` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `pushkin`
--

INSERT INTO `pushkin` (`id`, `title`, `img`, `text`, `price`) VALUES
(1, 'А.С. Пушкин \"Капитанская дочка\"', '/img/pushkin/1.jpg', '«Капита́нская до́чка» — исторический роман Александра Пушкина, действие которого происходит во время восстания Емельяна Пугачёва. Впервые опубликован без указания имени автора в 4-й книжке журнала «Современник», поступившей в продажу в последней декаде 1836 года.', 150),
(2, 'А.С. Пушкин \"Пиковая дама\"', '/img/pushkin/2.jpg', '«Пи́ковая да́ма» — повесть Александра Сергеевича Пушкина с мистическими элементами, послужившая источником сюжета одноимённой оперы П. И. Чайковского.', 100),
(3, 'А.С. Пушкин \"Руслан и Людмила\"', '/img/pushkin/3.jpg', '«Русла́н и Людми́ла» — первая законченная поэма Александра Сергеевича Пушкина; волшебная сказка, вдохновлённая древнерусскими былинами.', 200),
(4, 'А.С. Пушкин \"Повести...Белкина\"', '/img/pushkin/4.jpg', '«По́вести поко́йного Ива́на Петро́вича Бе́лкина» — цикл повестей Александра Сергеевича Пушкина, состоящий из 5 повестей и выпущенный им без указания имени настоящего автора, то есть самого Пушкина. Книга состоит из предисловия издателя и пяти повестей: «Выстрел» «Метель» «Гробовщик» «Станционный смотритель»', 120),
(5, 'А.С. Пушкин \"Дубровский\"', '/img/pushkin/5.jpg', '«Дубро́вский» — наиболее известный разбойничий роман на русском языке, необработанное для печати произведение А. С. Пушкина. Повествует о любви Владимира Дубровского и Марии Троекуровой — потомков двух враждующих помещичьих семейств.', 110),
(6, 'А.С. Пушкин \"Сказка о царе Салтане\"', '/img/pushkin/6.jpg', '«Сказка о царе Салта́не, о сыне его славном и могучем богатыре князе Гвидо́не Салта́новиче и о прекрасной царевне Лебеди» — сказка в стихах Александра Пушкина, написанная в 1831 году и впервые изданная в следующем году в собрании стихотворений.', 300),
(7, 'А.С. Пушкин \"Стихотворения\"', '/img/pushkin/7.jpg', 'ЛУЧШИЙ ПОЭТ!!! ОБЯЗАТЕЛЬНО К ПРОЧТЕНИЮ!!!', 250),
(8, 'А.С. Пушкин \"Стихотворения\"', '/img/pushkin/8.jpg', 'Вау, это же Александр Сергеевич Пушкин!!! Лучшие стихотворения великого поэта', 600),
(9, 'А.С. Пушкин \"Стихотворения и поэмы\"', '/img/pushkin/9.jpg', 'Алекса́ндр Серге́евич Пу́шкин — русский поэт, драматург и прозаик, заложивший основы русского реалистического направления, критик и теоретик литературы, историк, публицист; один из самых авторитетных литературных деятелей первой трети XIX века.', 300),
(10, 'А.С. Пушкин \"Я вас любил...\"', '/img/pushkin/10.jpg', '«Я вас любил…» — стихотворение Александра Сергеевича Пушкина, написанное в 1829 году и представляющее собой лирическую миниатюру, созданную, по мнению исследователей, как отклик на реальные события в жизни поэта. Мелодичность элегии образуется благодаря пятистопному ямбу с пиррихиями.', 100);

