--
-- Структура таблицы `tolstoy`
--

CREATE TABLE `tolstoy` (
  `id` int(3) NOT NULL,
  `title` varchar(50) DEFAULT NULL,
  `img` varchar(255) DEFAULT NULL,
  `text` text DEFAULT NULL,
  `price` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `tolstoy`
--

INSERT INTO `tolstoy` (`id`, `title`, `img`, `text`, `price`) VALUES
(1, 'Л.Н. Толстой \"Война и Мир\"', '/img/tolstoy/1.jpg', '«Война́ и мир» — роман-эпопея Льва Николаевича Толстого, описывающий русское общество в эпоху войн против Наполеона в 1805—1812 годах. Эпилог романа доводит повествование до 1820 года. ', 900),
(2, 'Л.Н. Толстой \"Анна Каренина\"', '/img/tolstoy/2.jpg', '«А́нна Каре́нина» — роман Льва Толстого о трагической любви замужней дамы Анны Карениной и блестящего офицера Вронского на фоне счастливой семейной жизни дворян Константина Лёвина и Кити Щербацкой.', 700),
(3, 'Л.Н. Толстой \"Смерть Ивана Ильича\"', '/img/tolstoy/3.jpg', '«Смерть Ива́на Ильича́» — повесть Л. Н. Толстого, над которой он работал с 1882 по 1886 год, внося последние штрихи уже на стадии корректуры. В произведении рассказывается о мучительном умирании судейского чиновника средней руки.', 550),
(4, 'Л.Н. Толстой \"Воскресение\"', '/img/tolstoy/4.jpg', '«Воскресе́ние» — последний роман Льва Николаевича Толстого, написанный им в 1889—1899 годах. Роман почти сразу после публикации был переведён на основные европейские языки.', 470),
(5, 'Л.Н. Толстой \"Хаджи-Мурат\"', '/img/tolstoy/5.jpg', '«Хаджи́-Мура́т» — повесть Льва Толстого, написанная в конце 1890-х — начале 1900-х и опубликованная в 1912 году, после смерти писателя. Главный герой повести — реальное историческое лицо, Хаджи-Мурат, наиб Шамиля, в 1851 году перешедший на сторону русских, а в следующем году погибший при попытке бежать в горы.', 340),
(6, 'Л.Н. Толстой \"Детсво. Отрочество. Юность\"', '/img/tolstoy/6.jpg', 'Де́тство» — первая повесть автобиографической трилогии Льва Толстого, впервые напечатана в 1852 году в журнале «Современник», № 9. Эта книга описывает психологические переживания, которые испытывают многие мальчики в детстве: первая влюблённость, чувство несправедливости, обида, стеснение. ', 800),
(7, 'Л.Н. Толстой \"Исповедь. О жизни\"', '/img/tolstoy/7.jpg', '«И́споведь» — автобиографическое произведение Льва Толстого, написанное в конце 1870-х — начале 1880-х годов и впервые опубликованное в 1884 году в Женеве.', 450),
(8, 'Л.Н. Толстой \"Крейцерова соната\"', '/img/tolstoy/8.jpg', '«Кре́йцерова сона́та» — повесть Льва Толстого, опубликованная в 1890 году и сразу подвергнутая цензуре царскими властями. Книга провозглашает идеал воздержания и описывает от первого лица гнев ревности.', 250),
(9, 'Л.Н. Толстой \"Казаки\"', '/img/tolstoy/9.jpg', '«Каза́ки» — опубликованная в 1863 году повесть Льва Толстого о пребывании юнкера в станице гребенских казаков.', 280),
(10, 'Л.Н. Толстой \"Семейное счастие\"', '/img/tolstoy/10.jpg', '«Семе́йное сча́стие» — роман Льва Толстого, впервые напечатанный в журнале «Русский вестник» в 1859 году, № 7, 8. Начало работы над романом относится к весне или осени 1858 года. В начале марта 1859 года была готова первая редакция. Через месяц она была переработана во вторую.', 700);

